    print(result)

import gurobipy as gp
from gurobipy import GRB
import math

def solve_MultiDepotVRP(
    customer_num = 10,
    center_num = 2,
    customer_demand = [2, 2, 9, 1, 4, 4, 10, 6, 4, 4],
    truck_num = [3, 3],  # 3 trucks at DC 1, 3 trucks at DC 2
    truck_capacity = [20, 18],  # Capacity of trucks at DC 1 and DC 2
    customer_coord = [(-4.53, -5.12), (2.14, 5.85), (-4.88, -8.33), (2.24, 1.63),
                    (3.91, -5.88), (-1.1, 0.25), (7.92, -3.4), (-1.89, -5.0),
                    (0.72, -8.8), (-1.88, -2.89)],
    center_coord = [(-5, -5), (5, 5)]
):
    # --- Model Initialization ---
    model = gp.Model("MultiDepotVRP")


    # Define sets
    customers = range(1, customer_num + 1)  # Customers are 1 to 10
    dcs = range(customer_num + 1,
                customer_num + center_num + 1)  # DCs are 11 and 12
    nodes = list(customers) + list(dcs)  # All nodes

    # Create trucks for each distribution center
    all_trucks = []
    dc_to_trucks = {}
    for i, dc in enumerate(dcs):
        dc_trucks = list(range(sum(truck_num[:i]) + 1, sum(truck_num[:i + 1]) + 1))
        dc_to_trucks[dc] = dc_trucks
        all_trucks.extend(dc_trucks)

    # Create a list of all coordinates for visualization
    all_coords = {}
    for i, coord in enumerate(customer_coord):
        all_coords[i + 1] = coord  # Customer i+1 has coordinates coord
    for i, coord in enumerate(center_coord):
        all_coords[customer_num + i + 1] = coord  # DC i+1 has coordinates coord

    # Calculate distances between nodes
    distance = {}
    for i in nodes:
        for j in nodes:
            if i != j:  # No self-loops
                x1, y1 = all_coords[i]
                x2, y2 = all_coords[j]
                distance[i, j] = math.sqrt((x2 - x1)**2 + (y2 - y1)**2)

    # --- Decision variables ---
    # x[i,j,k] = 1 if truck k travels from node i to node j, 0 otherwise
    x = {}
    for i in nodes:
        for j in nodes:
            if i != j:  # No self-loops
                for k in all_trucks:
                    x[i, j, k] = model.addVar(vtype=GRB.BINARY,
                                            name=f"x_{i}_{j}_{k}")

    # --- Objective function ---
    # minimize total distance traveled
    objective = gp.quicksum(distance[i, j] * x[i, j, k] for i in nodes
                            for j in nodes for k in all_trucks if i != j)
    model.setObjective(objective, GRB.MINIMIZE)

    # --- Constraints ---
    # Constraint 1: Each customer must be visited exactly once (by one truck)
    for j in customers:
        model.addConstr(gp.quicksum(x[i, j, k] for i in nodes if i != j
                                    for k in all_trucks) == 1,
                        name=f"customer_visited_{j}")

    # Constraint 2: Flow conservation - a truck that enters a customer must also leave it
    for i in customers:
        for k in all_trucks:
            model.addConstr(gp.quicksum(x[i, j, k] for j in nodes if j != i) -
                            gp.quicksum(x[j, i, k] for j in nodes if j != i) == 0,
                            name=f"flow_conservation_{i}_{k}")

    # Constraint 3: Each truck leaves its distribution center exactly once
    for d in dcs:
        for k in dc_to_trucks[d]:
            model.addConstr(gp.quicksum(x[d, j, k] for j in nodes if j != d) == 1,
                            name=f"truck_{k}_leaves_dc_{d}")

    # Constraint 4: Each truck returns to its distribution center
    for d in dcs:
        for k in dc_to_trucks[d]:
            model.addConstr(gp.quicksum(x[i, d, k] for i in customers) -
                            gp.quicksum(x[d, j, k] for j in customers) == 0,
                            name=f"truck_{k}_returns_to_dc_{d}")

    # Constraint 5: Capacity constraint for each truck
    for d_idx, d in enumerate(dcs):
        for k in dc_to_trucks[d]:
            model.addConstr(gp.quicksum(
                customer_demand[j - 1] * gp.quicksum(x[i, j, k]
                                                    for i in nodes if i != j)
                for j in customers) <= truck_capacity[d_idx],
                            name=f"truck_{k}_capacity")


    # Function to find subtours in the current solution
    def find_subtours(x_vals, k):
        """Find subtours in the current solution for truck k"""
        # Create an edge list from the solution
        edges = [(i, j) for i in nodes for j in nodes
                if i != j and (i, j, k) in x_vals and x_vals[i, j, k] > 0.5]

        if not edges:
            return []

        # Create an undirected graph for finding connected components
        neighbors = {i: [] for i in nodes}
        for i, j in edges:
            neighbors[i].append(j)

        # Find all connected components (subtours)
        unvisited = set(customers)  # Only consider customer nodes for subtours
        subtours = []

        while unvisited:
            # Start a new tour with an unvisited node
            if not unvisited:
                break
            curr = next(iter(unvisited))
            tour = [curr]
            unvisited.remove(curr)

            # Build the tour
            while neighbors[curr]:
                neighbors_of_curr = [j for j in neighbors[curr] if j in unvisited]
                if not neighbors_of_curr:
                    break

                curr = neighbors_of_curr[0]
                if curr in unvisited:  # Only add if not already in tour
                    tour.append(curr)
                    unvisited.remove(curr)

            # Check if it's a subtour (not connected to any distribution center)
            is_connected_to_dc = any(d in neighbors[node] for node in tour
                                    for d in dcs)

            if not is_connected_to_dc and len(tour) > 1:
                subtours.append(tour)

        return subtours


    # Callback function for lazy constraints
    def subtour_elimination_callback(model, where):
        if where == GRB.Callback.MIPSOL:
            # Get the current solution values
            x_vals = {}
            for i in nodes:
                for j in nodes:
                    if i != j:
                        for k in all_trucks:
                            if (i, j, k) in x:
                                val = model.cbGetSolution(x[i, j, k])
                                if val > 0.5:
                                    x_vals[i, j, k] = val

            # For each truck, find subtours and add constraints
            for k in all_trucks:
                subtours = find_subtours(x_vals, k)
                for subtour in subtours:
                    if len(subtour
                        ) >= 2:  # Only add constraints for actual subtours
                        # Add subtour elimination constraint
                        model.cbLazy(
                            gp.quicksum(x[i, j, k] for i in subtour
                                        for j in subtour
                                        if i != j) <= len(subtour) - 1)


    # Enable lazy constraints
    model._vars = x
    model.Params.lazyConstraints = 1

    # Optimize the model with the callback
    model.optimize(subtour_elimination_callback)

    # Check if the model was solved to optimality
    if model.status == GRB.OPTIMAL:
        return {"status": "optimal", "obj": model.objVal}
    else:
        return {"status": f"{model.status}"}

if __name__ == "__main__":
    result = solve_MultiDepotVRP()
    print(result)

import gurobipy as gp
from gurobipy import GRB
import math


def solve_tsp(
    coordinates=[(0, 0), (3.5, 4.2), (-2.1, 5.6), (4.8, -3.2), (-3.6, -2.8),
                 (5.2, 2.4), (-4.1, 3.7), (2.8, -4.5), (6.3, -2.1),
                 (-5.2, -1.8), (3.9, 5.8)]):
    """
    Solves the Traveling Salesperson Problem (TSP) for community health services.
    """
    community_num = len(coordinates) - 1
    nodes = range(community_num + 1
                  )  # 0 is the central hospital, 1-10 are communities

    # Calculate distance matrix
    def calculate_distance(coord1, coord2):
        return math.sqrt((coord1[0] - coord2[0])**2 +
                         (coord1[1] - coord2[1])**2)

    distance = {}
    for i in nodes:
        for j in nodes:
            if i != j:
                distance[i,
                         j] = calculate_distance(coordinates[i], coordinates[j])

    # Create a new model
    model = gp.Model("TSP")

    # Decision variables: x[i,j] = 1 if the path goes from i to j
    x = {}
    for i in nodes:
        for j in nodes:
            if i != j:
                x[i, j] = model.addVar(vtype=GRB.BINARY, name=f'x_{i}_{j}')

    # Set objective: minimize total distance
    objective = gp.quicksum(distance[i, j] * x[i, j] for i in nodes
                            for j in nodes if i != j)
    model.setObjective(objective, GRB.MINIMIZE)

    # Constraint 1: Each node must be visited exactly once
    for j in nodes:
        model.addConstr(gp.quicksum(x[i, j] for i in nodes if i != j) == 1,
                        name=f'visit_to_{j}')

    # Constraint 2: Must leave each node exactly once
    for i in nodes:
        model.addConstr(gp.quicksum(x[i, j] for j in nodes if i != j) == 1,
                        name=f'leave_from_{i}')

    # Constraint 3: Subtour elimination using subset constraints
    def find_subtours(edges):
        # Create adjacency list
        adj = {i: [] for i in nodes}
        for i, j in edges:
            adj[i].append(j)

        # Find all subtours
        unvisited = set(nodes)
        subtours = []
        while unvisited:
            # Start from any unvisited node
            current = next(iter(unvisited))
            subtour = []
            while current in unvisited:
                unvisited.remove(current)
                subtour.append(current)
                # Move to next node in path
                next_nodes = adj.get(current, [])
                if not next_nodes:
                    break
                current = next_nodes[0]
            if len(subtour) < len(nodes):
                subtours.append(subtour)
        return subtours

    # Callback function for lazy constraints
    def subtour_cb(model, where):
        if where == GRB.Callback.MIPSOL:
            # Get values of decision variables
            x_vals = model.cbGetSolution(model._vars)
            edges = [(i, j) for i in nodes for j in nodes
                     if i != j and x_vals[i, j] > 0.5]

            # Find subtours
            subtours = find_subtours(edges)

            # Add subtour elimination constraints
            for S in subtours:
                if len(S) < len(nodes):
                    model.cbLazy(
                        gp.quicksum(x[i, j] for i in S for j in S
                                    if i != j) <= len(S) - 1)

    # Enable lazy constraints
    model._vars = x
    model.Params.lazyConstraints = 1

    # Optimize model with callback
    model.optimize(subtour_cb)

    # Return results
    if model.status == GRB.OPTIMAL:
        return {"status": "optimal", "obj": model.ObjVal}
    else:
        return {"status": f"{model.status}"}


# Run the solver function
if __name__ == "__main__":
    result = solve_tsp()
    print(result)

import gurobipy as gp
from gurobipy import GRB
import math


def solve_PickupDeliveryVRP(
    order_num=10,
    truck_num=3,
    speed=30,
    maxload=1000,
    M=1e5,
    coordinates = [(0, 0), (1.41, 2.64), (6.34, -4.45), (3.04, 7.82),
                (8.18, -6.27), (3.03, 2.34), (0.07, 9.32), (0.57, -1.09),
                (8.87, 2.71), (-3.97, -3.81), (-0.08, -8.95), (8.56, 1.11),
                (3.63, -7.65), (-8.88, 9.6), (-4.31, -5.73), (7.93, 5.0),
                (-7.54, -3.57), (-5.15, -4.55), (-9.87, 0.49), (-3.91, -2.12),
                (-6.29, -7.8)],
    demands = [
        0, 198, 252, 94, 223, 163, 247, 133, 188, 216, 225, -198, -252, -94, -223,
        -163, -247, -133, -188, -216, -225
    ],
    time_windows = [(0, 1440), (570, 600), (720, 750), (630, 660), (720, 750),
                    (750, 780), (510, 540), (480, 510), (840, 870), (600, 630),
                    (570, 600), (580, 640), (730, 760), (650, 680), (720, 750),
                    (780, 810), (540, 570), (490, 520), (870, 900), (630, 660),
                    (580, 610)]
):
    # --- Model Initialization ---
    model = gp.Model("PickupDeliveryVRP")

    # Define sets
    nodes = range(order_num * 2 + 1)  # 0 is depot, 1-10 are pickup, 11-20 are delivery
    pickup_nodes = range(1, order_num + 1)
    trucks = range(truck_num)

    # Calculate distance matrix
    def calculate_distance(coord1, coord2):
        return math.sqrt((coord1[0] - coord2[0])**2 + (coord1[1] - coord2[1])**2)


    distance = {}
    for i in nodes:
        for j in nodes:
            if i != j:
                distance[i, j] = calculate_distance(coordinates[i], coordinates[j])

    # --- Decision variables ---
    # x[i,j,k] = 1 if truck k travels from i to j
    x = {}
    for i in nodes:
        for j in nodes:
            if i != j:
                for k in trucks:
                    x[i, j, k] = model.addVar(vtype=GRB.BINARY,
                                            name=f'x_{i}_{j}_{k}')

    # t_a[i] = arrival time at node i
    t_a = {}
    for i in nodes:
        t_a[i] = model.addVar(lb=time_windows[i][0],
                            ub=time_windows[i][1],
                            name=f't_a_{i}')

    # t_d[i] = departure time from node i
    t_d = {}
    for i in nodes:
        t_d[i] = model.addVar(lb=time_windows[i][0],
                            ub=time_windows[i][1],
                            name=f't_d_{i}')

    # load[i,k] = load of truck k when departing from node i
    load = {}
    for i in nodes:
        for k in trucks:
            load[i, k] = model.addVar(lb=0, ub=maxload, name=f'load_{i}_{k}')

    # --- Objective function ---
    # minimize total distance
    objective = gp.quicksum(distance[i, j] * x[i, j, k] for i in nodes
                            for j in nodes for k in trucks if i != j)
    model.setObjective(objective, GRB.MINIMIZE)

    # --- Constraints ---
    # Constraint 1: Each pickup & delivery point must be visited exactly once by one truck
    for j in nodes:
        if j != 0:  # Skip depot
            model.addConstr(gp.quicksum(x[i, j, k] for i in nodes for k in trucks
                                        if i != j) == 1,
                            name=f'visit_to_{j}')

    # Constraint 2: One order's pickup and delivery points must be visited by the same truck
    for i in pickup_nodes:
        for k in trucks:
            model.addConstr(gp.quicksum(x[i, j, k] for j in nodes
                                        if j != i) == gp.quicksum(
                                            x[i + order_num, j, k] for j in nodes
                                            if j != i + order_num),
                            name=f'same_truck_{i}_{k}')

    # Constraint 3: Flow conservation for each point
    for i in nodes:
        for k in trucks:
            model.addConstr(gp.quicksum(x[i, j, k] for j in nodes
                                        if j != i) == gp.quicksum(x[j, i, k]
                                                                for j in nodes
                                                                if j != i),
                            name=f'flow_cons_{i}_{k}')

    # Constraint 4: Each truck must leave the depot once
    for k in trucks:
        model.addConstr(gp.quicksum(x[0, j, k] for j in nodes if j != 0) == 1,
                        name=f'truck_{k}_leaves_depot')

    # Constraint 5: The order's pickup point must be visited before its delivery point
    for i in pickup_nodes:
        model.addConstr(t_a[i] <= t_d[i + order_num],
                        name=f'pickup_before_delivery_{i}')

    # Constraints 6 & 7: Time window constraints (already handled by variable bounds)

    # Constraint 8: Time consistency constraints
    for i in nodes:
        for j in nodes:
            if i != j:
                for k in trucks:
                    # If truck k goes from i to j, arrival time at j must be after departure from i plus travel time
                    model.addConstr(t_d[i] + 60 * distance[i, j] / speed - M *
                                    (1 - x[i, j, k]) <= t_a[j],
                                    name=f'time_cons_{i}_{j}_{k}')

    # Constraint 9: The truck's load must not exceed its maximum load (handled by variable bounds)

    # Constraint 10: The truck's load must be updated at every visited point
    for i in nodes:
        for j in nodes:
            if i != j:
                for k in trucks:
                    model.addConstr(load[j, k] >= load[i, k] + demands[j] - M *
                                    (1 - x[i, j, k]),
                                    name=f'load_update_lb_{i}_{j}_{k}')
                    model.addConstr(load[j, k] <= load[i, k] + demands[j] + M *
                                    (1 - x[i, j, k]),
                                    name=f'load_update_ub_{i}_{j}_{k}')

    # Constraint 11: The truck's load must be initialized at the depot
    for k in trucks:
        model.addConstr(load[0, k] == 0, name=f'initial_load_{k}')


    # Constraint 12: Eliminate sub-tours (using lazy constraints approach)
    def find_subtours(edges):
        # Create adjacency list
        adj = {i: [] for i in nodes}
        for i, j in edges:
            adj[i].append(j)

        # Find all subtours
        unvisited = set(nodes)
        subtours = []
        while unvisited:
            current = next(iter(unvisited))
            subtour = []
            while current in unvisited:
                unvisited.remove(current)
                subtour.append(current)
                next_nodes = adj.get(current, [])
                if not next_nodes:
                    break
                current = next_nodes[0]
            if len(subtour) < len(nodes):
                subtours.append(subtour)
        return subtours


    def subtour_cb(model, where):
        if where == GRB.Callback.MIPSOL:
            # Get values of binary variables
            x_vals = model.cbGetSolution(model._vars)
            for k in trucks:
                edges = [
                    (i, j) for i in nodes for j in nodes
                    if i != j and (i, j, k) in x_vals and x_vals[i, j, k] > 0.5
                ]

                subtours = find_subtours(edges)

                # Add subtour elimination constraints
                for S in subtours:
                    if len(S) < len(
                            nodes
                    ) and 0 not in S:  # Exclude depot from subtour check
                        model.cbLazy(
                            gp.quicksum(x[i, j, k] for i in S for j in S
                                        if i != j) <= len(S) - 1)


    # Enable lazy constraints
    model._vars = x
    model.Params.lazyConstraints = 1

    # Set a time limit (optional)
    model.Params.timeLimit = 3600  # 1 hour

    # Optimize model with callback
    model.optimize(subtour_cb)

    if model.status == GRB.OPTIMAL:
        return {"status": "optimal", "obj": model.objVal}
    else:
        return {"status": f"{model.status}"}


if __name__ == "__main__":
    result = solve_PickupDeliveryVRP()
    print(result)

import gurobipy as gp
from gurobipy import GRB


def solve_textile_manufacturing(
    ProducedFrom={
        "cotton_fabric": ["cotton", "jute"],
        "linen_fabric": ["linen", "jute", "cotton"],
        "jute_fabric": ["jute", "linen"]
    },
    ReducedFiberRatio={
        "cotton_fabric": 0.7,
        "jute_fabric": 0.75,
        "linen_fabric": 0.8
    },
    PurchasingCost={
        "cotton": 350,
        "jute": 400,
        "linen": 300
    },
    ProcessingCost={
        "cotton": 100,
        "jute": 80,
        "linen": 150
    },
    Availability={
        "cotton": 1000,
        "jute": 800,
        "linen": 600
    },
    Demand={
        "cotton_fabric": 300,
        "jute_fabric": 400,
        "linen_fabric": 200
    }):
    """
    Solves the textile manufacturing optimization problem.
    """
    # Create a new model
    model = gp.Model("Textile Manufacturing Optimization")

    # Sets
    RawMaterials = list(PurchasingCost.keys())
    Products = list(Demand.keys())

    # Decision variables
    X = model.addVars(RawMaterials, Products, vtype=GRB.CONTINUOUS, name="X")

    # Objective: Minimize total cost
    obj = gp.quicksum(
        (PurchasingCost[r] + ProcessingCost[r]) * X[r, p] for p in Products
        for r in RawMaterials if r in ProducedFrom.get(p, []))
    model.setObjective(obj, GRB.MINIMIZE)

    # Constraint 1: Demand constraint
    for p in Products:
        model.addConstr(
            gp.quicksum(ReducedFiberRatio[p] * X[r, p]
                        for r in ProducedFrom[p]) >= Demand[p], f"Demand_{p}")

    # Constraint 2: Availability constraint
    for r in RawMaterials:
        model.addConstr(
            gp.quicksum(X[r, p] for p in Products
                        if r in ProducedFrom.get(p, [])) <= Availability[r],
            f"Availability_{r}")

    # Optimize the model
    model.optimize()

    # Return results
    if model.status == GRB.OPTIMAL:
        return {"status": "optimal", "obj": model.ObjVal}
    else:
        return {"status": f"{model.status}"}


if __name__ == "__main__":
    result = solve_textile_manufacturing()
    print(result)

