import gurobipy as gp
from gurobipy import GRB


def solve_textile_manufacturing(
    ProducedFrom={
        "cotton_fabric": ["cotton", "jute"],
        "linen_fabric": ["linen", "jute", "cotton"],
        "jute_fabric": ["jute", "linen"]
    },
    ReducedFiberRatio={
        "cotton_fabric": 0.7,
        "jute_fabric": 0.75,
        "linen_fabric": 0.8
    },
    PurchasingCost={
        "cotton": 350,
        "jute": 400,
        "linen": 300
    },
    ProcessingCost={
        "cotton": 100,
        "jute": 80,
        "linen": 150
    },
    Availability={
        "cotton": 1000,
        "jute": 800,
        "linen": 600
    },
    Demand={
        "cotton_fabric": 300,
        "jute_fabric": 400,
        "linen_fabric": 200
    }):
    """
    Solves the textile manufacturing optimization problem.
    """
    # Create a new model
    model = gp.Model("Textile Manufacturing Optimization")

    # Sets
    RawMaterials = list(PurchasingCost.keys())
    Products = list(Demand.keys())

    # Decision variables
    X = model.addVars(RawMaterials, Products, vtype=GRB.CONTINUOUS, name="X")

    # Objective: Minimize total cost
    obj = gp.quicksum(
        (PurchasingCost[r] + ProcessingCost[r]) * X[r, p] for p in Products
        for r in RawMaterials if r in ProducedFrom.get(p, []))
    model.setObjective(obj, GRB.MINIMIZE)

    # Constraint 1: Demand constraint
    for p in Products:
        model.addConstr(
            gp.quicksum(ReducedFiberRatio[p] * X[r, p]
                        for r in ProducedFrom[p]) >= Demand[p], f"Demand_{p}")

    # Constraint 2: Availability constraint
    for r in RawMaterials:
        model.addConstr(
            gp.quicksum(X[r, p] for p in Products
                        if r in ProducedFrom.get(p, [])) <= Availability[r],
            f"Availability_{r}")

    # Optimize the model
    model.optimize()

    # Return results
    if model.status == GRB.OPTIMAL:
        return {"status": "optimal", "obj": model.ObjVal}
    else:
        return {"status": f"{model.status}"}


if __name__ == "__main__":
    result = solve_textile_manufacturing()
    print(result)

import gurobipy as gp
from gurobipy import GRB


def solve_fund_allocation(
    initial_funds=50000.0,
    revenues={1: 20000.0, 2: 25000.0, 3: 30000.0, 4: 35000.0},
    expenses={1: 15000.0, 2: 35000.0, 3: 22000.0, 4: 28000.0},
    monthly_interest_rates={
        1: 0.002,
        2: 0.008,
        3: 0.015,
        4: 0.030
    }
):
    """
    Solves the SupplyChain Dynamics fund allocation problem.
    """
    try:
        # --- 1. Model Data ---
        months_invest = [1, 2, 3, 4]
        months_balance = [1, 2, 3, 4]
        investment_durations = [1, 2, 3, 4]

        # --- 2. Create Gurobi Model ---
        model = gp.Model("SupplyChainFundAllocation")
        model.Params.LogToConsole = 0

        # --- 3. Decision Variables ---
        X = model.addVars([(t, d) for t in months_invest for d in investment_durations if t + d <= 5],
                          name="InvestAmount", lb=0.0, vtype=GRB.CONTINUOUS)
        C = model.addVars(months_balance, name="CashOnHandEndOfMonth", lb=0.0, vtype=GRB.CONTINUOUS)

        # --- 4. Objective Function ---
        cash_at_month_5_start = C[4] + gp.quicksum(
            X[t, d] * (1 + d * monthly_interest_rates[d])
            for t in months_invest for d in investment_durations if (t, d) in X and t + d == 5)
        model.setObjective(cash_at_month_5_start, GRB.MAXIMIZE)

        # --- 5. Constraints ---
        for t in months_invest:
            if t == 1:
                funds_in = initial_funds + revenues[t] - expenses[t]
            else:
                cash_from_last_month = C[t - 1]
                maturing_investments_value = gp.quicksum(
                    X[t_invest, d_invest] * (1 + d_invest * monthly_interest_rates[d_invest])
                    for t_invest in months_invest for d_invest in investment_durations
                    if (t_invest, d_invest) in X and t_invest + d_invest == t)
                funds_in = cash_from_last_month + maturing_investments_value + revenues[t] - expenses[t]

            new_investments_made_this_month = gp.quicksum(X[t, d] for d in investment_durations if (t, d) in X)
            cash_carried_over = C[t]
            model.addConstr(funds_in == new_investments_made_this_month + cash_carried_over, name=f"CashBalance_Month{t}")
            if t == 1:
                model.addConstr(expenses[t] <= initial_funds + revenues[t], name=f"Expenses_Month{t}")
            else:
                model.addConstr(expenses[t] <= C[t - 1] + revenues[t], name=f"Expenses_Month{t}")

        # --- 6. Solve Model ---
        model.optimize()

        # --- 7. Return Results ---
        if model.Status == GRB.OPTIMAL:
            return {"status": "optimal", "obj": model.ObjVal}
        else:
            return {"status": f"{model.Status}"}

    except gp.GurobiError as e:
        return {"status": f"Gurobi error: {e}"}
    except Exception as e:
        return {"status": f"An unexpected error occurred: {e}"}


if __name__ == "__main__":
    result = solve_fund_allocation()
    print(result)

import gurobipy as gp
from gurobipy import GRB


def solve_drone_tsp(
    flight_times_input=[
        [0, 8, 12, 15, 7, 10, 14],
        [8, 0, 5, 11, 6, 9, 13],
        [12, 5, 0, 7, 10, 4, 8],
        [15, 11, 7, 0, 9, 6, 5],
        [7, 6, 10, 9, 0, 3, 11],
        [10, 9, 4, 6, 3, 0, 7],
        [14, 13, 8, 5, 11, 7, 0]
    ]
):
    """
    Solves the Drone Traveling Salesperson Problem (TSP).
    """
    try:
        # --- Parameters ---
        customer_locations_set = {"C1", "C2", "C3", "C4", "C5", "C6"}
        depot_name = "Depot"
        location_names = [depot_name] + sorted(list(customer_locations_set))
        num_total_locations = len(location_names)
        depot_index = 0
        customer_indices = list(range(1, num_total_locations))

        flight_time_matrix = {
            (i, j): flight_times_input[i][j]
            for i in range(num_total_locations)
            for j in range(num_total_locations)
        }

        # --- Create a new model ---
        model = gp.Model("DroneTSP")

        # --- Decision Variables ---
        arcs = [(i, j) for i in range(num_total_locations) for j in range(num_total_locations) if i != j]
        x = model.addVars(arcs, vtype=GRB.BINARY, name="Route")

        # --- Objective Function ---
        model.setObjective(gp.quicksum(flight_time_matrix[i, j] * x[i, j] for i, j in arcs), GRB.MINIMIZE)

        # --- Constraints ---
        model.addConstr(gp.quicksum(x[depot_index, j] for j in customer_indices) == 1, "LeaveDepot")
        model.addConstr(gp.quicksum(x[i, depot_index] for i in customer_indices) == 1, "ReturnToDepot")

        for j in customer_indices:
            model.addConstr(gp.quicksum(x[i, j] for i in range(num_total_locations) if i != j) == 1, f"EnterCustomer_{j}")

        for i in customer_indices:
            model.addConstr(gp.quicksum(x[i, j] for j in range(num_total_locations) if i != j) == 1, f"LeaveCustomer_{i}")

        num_customers = len(customer_indices)
        u = model.addVars(customer_indices, lb=1.0, ub=float(num_customers), vtype=GRB.CONTINUOUS, name="u")

        for i in customer_indices:
            for j in customer_indices:
                if i != j:
                    model.addConstr(u[i] - u[j] + num_customers * x[i, j] <= num_customers - 1, f"MTZ_{i}_{j}")

        # --- Optimize model ---
        model.optimize()

        # --- Return solution ---
        if model.status == GRB.OPTIMAL:
            return {"status": "optimal", "obj": model.ObjVal}
        else:
            return {"status": f"{model.status}"}

    except gp.GurobiError as e:
        return {"status": f"Gurobi error: {e}"}
    except Exception as e:
        return {"status": f"An unexpected error occurred: {e}"}


if __name__ == "__main__":
    result = solve_drone_tsp()
    print(result)

