import gurobipy as gp
from gurobipy import GRB

def solve_warehouse_distribution(
    products=['A', 'B', 'C', 'D', 'E'],
    stores=['Store1', 'Store2', 'Store3'],
    trucks=[1, 2, 3, 4],
    product_weights={'A': 2, 'B': 3, 'C': 1, 'D': 4, 'E': 2},
    truck_capacity=15,
    store_demands={
        'Store1': {'A': 2, 'B': 1, 'C': 3, 'D': 0, 'E': 0},
        'Store2': {'A': 1, 'B': 0, 'C': 0, 'D': 2, 'E': 1},
        'Store3': {'A': 0, 'B': 3, 'C': 2, 'D': 0, 'E': 1}
    }
):
    """
    Solves the warehouse distribution problem using Gurobi.
    Minimizes the number of trucks used to deliver products to stores while meeting demand and capacity constraints.
    """
    # Create model
    model = gp.Model("WarehouseDistribution")

    # Decision variables
    # x[p,s,t]: quantity of product p delivered to store s using truck t
    x = model.addVars(products, stores, trucks, vtype=GRB.INTEGER, name="ProductDelivery")

    # y[t]: binary variable indicating if truck t is used
    y = model.addVars(trucks, vtype=GRB.BINARY, name="TruckUsed")

    # Objective: Minimize total trucks used
    model.setObjective(y.sum(), GRB.MINIMIZE)

    # Constraints
    # 1. Meet all store demands
    for s in stores:
        for p in products:
            model.addConstr(
                gp.quicksum(x[p, s, t] for t in trucks) == store_demands[s][p],
                name=f"Demand_{s}_{p}"
            )

    # 2. Truck capacity limit
    for t in trucks:
        model.addConstr(
            gp.quicksum(product_weights[p] * x[p, s, t] for p in products for s in stores)
            <= truck_capacity * y[t],
            name=f"Capacity_{t}"
        )

    # 3. Logical constraint: if any product is delivered by truck t, y_t must be 1
    # Using a large M (here M = max possible demand for any product)
    M = max(max(demands.values()) for demands in store_demands.values())
    for p in products:
        for s in stores:
            for t in trucks:
                model.addConstr(
                    x[p, s, t] <= M * y[t],
                    name=f"Logic_{p}_{s}_{t}"
                )

    # Solve the model
    model.optimize()

    # Return results
    if model.status == GRB.OPTIMAL:
        return {"status": "optimal", "obj": model.ObjVal}
    else:
        return {"status": f"{model.status}"}


if __name__ == "__main__":
    result = solve_warehouse_distribution()
    print(result)

import gurobipy as gp
from gurobipy import GRB


def solve_beverage_distribution(
    N=[1, 2, 3, 4, 5, 6],
    A=[(1, 2), (1, 3), (2, 3), (2, 4), (3, 5), (3, 6), (4, 5), (5, 6)],
    P=['A', 'B', 'C', 'D', 'E'],
    source=1,
    sinks=[4, 5, 6],
    u={
        (1, 2): 20, (1, 3): 15, (2, 3): 10, (2, 4): 12,
        (3, 5): 18, (3, 6): 10, (4, 5): 8, (5, 6): 15
    },
    c={
        (1, 2): 3, (1, 3): 4, (2, 3): 2, (2, 4): 5,
        (3, 5): 3, (3, 6): 6, (4, 5): 1, (5, 6): 2
    },
    d={
        (4, 'A'): 3, (4, 'B'): 2, (4, 'C'): 3,
        (5, 'B'): 4, (5, 'D'): 5, (5, 'E'): 3,
        (6, 'A'): 2, (6, 'C'): 3, (6, 'E'): 5
    },
    supply_limit=30
):
    """Solve the multi-commodity beverage distribution problem using Gurobi."""
    # Create model
    model = gp.Model("BeverageDistribution")

    # ========== Decision Variables ==========
    # Flow of product p on arc (i,j)
    x = model.addVars(A, P, lb=0, name="flow")

    # ========== Objective Function ==========
    # Minimize total transportation cost
    model.setObjective(
        gp.quicksum(c[i, j] * x[i, j, p] for (i, j) in A for p in P),
        GRB.MINIMIZE
    )

    # ========== Constraints ==========
    # 1. Flow conservation at intermediate nodes (nodes 2,3)
    for i in [2, 3]:
        for p in P:
            model.addConstr(
                gp.quicksum(x[i, j, p] for (i_, j) in A if i_ == i) -
                gp.quicksum(x[j, i, p] for (j, i_) in A if i_ == i) == 0,
                f"flow_conservation_node_{i}_product_{p}"
            )

    # 2. Demand satisfaction at sink nodes
    for i in sinks:
        for p in P:
            if (i, p) in d:
                model.addConstr(
                    gp.quicksum(x[j, i, p] for (j, i_) in A if i_ == i) -
                    gp.quicksum(x[i, j, p] for (i_, j) in A if i_ == i) == d[i, p],
                    f"demand_satisfaction_node_{i}_product_{p}"
                )
            else:
                # No demand for this product at this node
                model.addConstr(
                    gp.quicksum(x[j, i, p] for (j, i_) in A if i_ == i) -
                    gp.quicksum(x[i, j, p] for (i_, j) in A if i_ == i) == 0,
                    f"no_demand_node_{i}_product_{p}"
                )

    # 3. Arc capacity constraints
    for (i, j) in A:
        model.addConstr(
            gp.quicksum(x[i, j, p] for p in P) <= u[i, j],
            f"arc_capacity_{i}_{j}"
        )

    # 4. Source supply limit
    model.addConstr(
        gp.quicksum(x[source, j, p] for (i, j) in A if i == source for p in P) <= supply_limit,
        "source_supply_limit"
    )

    # ========== Solve the Model ==========
    model.optimize()

    # ========== Return Results ==========
    if model.status == GRB.OPTIMAL:
        return {"status": "optimal", "obj": model.ObjVal}
    else:
        return {"status": f"{model.status}"}


# Execute the function
if __name__ == "__main__":
    result = solve_beverage_distribution()
    print(result)

import gurobipy as gp
from gurobipy import GRB
from typing import List, Dict, Tuple
import math

def solve_courier_routing(
    Customers: List[int] = [i for i in range(1, 11)],
    Depot: int = 0,
    Positions: Dict[int, Tuple[float, float]] = {
        0: (0, 0), 1: (3, 5), 2: (5, 8), 3: (2, 1), 4: (8, 6), 5: (4, 2), 6: (9, 8), 7: (1, 9), 8: (1, 3), 9: (6, 4), 10: (7, 1)
    },
    ServiceTime: Dict[int, int] = {1: 5, 2: 8, 3: 5, 4: 10, 5: 6, 6: 7, 7: 9, 8: 5, 9: 12, 10: 6, 0: 0},
    TimeWindows: Dict[int, Tuple[str, str]] = {
        1: ("9:30", "10:00"), 2: ("10:00", "10:20"), 3: ("9:00", "9:20"),
        4: ("10:30", "11:00"), 5: ("9:45", "10:15"), 6: ("11:15", "11:40"),
        7: ("10:10", "10:40"), 8: ("9:15", "9:40"), 9: ("10:20", "10:50"),
        10: ("11:00", "11:20")
    },
    DepartureTimeStr: str = "8:30",
    TravelSpeed_kmh: float = 30.0,
    TravelCostRate_per_km: float = 5.0,
    PenaltyCoefficient: float = 0.2
):
    """
    Solves the courier routing problem with soft time windows and a quadratic penalty function.
    This version includes corrections for sub-tour elimination.
    """
    # --- Data Pre-processing ---
    Locations = [Depot] + Customers

    def time_to_minutes(t_str: str) -> int:
        h, m = map(int, t_str.split(':'))
        return h * 60 + m

    DepartureTime = time_to_minutes(DepartureTimeStr)
    TimeWindowCenter = {
        c: (time_to_minutes(tw[0]) + time_to_minutes(tw[1])) / 2
        for c, tw in TimeWindows.items()
    }

    def euclidean_distance(p1, p2):
        return math.sqrt((p1[0] - p2[0])**2 + (p1[1] - p2[1])**2)

    Distances = {
        (i, j): euclidean_distance(Positions[i], Positions[j])
        for i in Locations for j in Locations if i != j
    }

    TravelTimes = {
        (i, j): (Distances[i, j] / TravelSpeed_kmh) * 60
        for i, j in Distances
    }

    # A large number for Big-M constraints
    M = 24 * 60 # Max minutes in a day

    # --- Model Initialization ---
    model = gp.Model("CourierRoutingWithTimeWindows")

    # --- Decision Variables ---
    x = model.addVars(Locations, Locations, name="Path", vtype=GRB.BINARY)
    t = model.addVars(Locations, name="ArrivalTime", vtype=GRB.CONTINUOUS, lb=0)
    u = model.addVars(Locations, name="Sequence", vtype=GRB.CONTINUOUS, lb=0)

    # --- Objective Function ---
    travel_cost = gp.quicksum(TravelCostRate_per_km * Distances[i, j] * x[i, j] 
                              for i in Locations for j in Locations if i != j)

    penalty_cost = gp.quicksum(PenaltyCoefficient * (t[i] - TimeWindowCenter[i]) * (t[i] - TimeWindowCenter[i])
                               for i in Customers)

    model.setObjective(travel_cost + penalty_cost, GRB.MINIMIZE)

    # --- Constraints ---
    model.addConstrs((x[i, i] == 0 for i in Locations), "NoSelfLoops")
    model.addConstr(gp.quicksum(x[Depot, j] for j in Customers) == 1, "LeaveDepot")
    model.addConstr(gp.quicksum(x[i, Depot] for i in Customers) == 1, "ReturnToDepot")
    model.addConstrs((gp.quicksum(x[i, j] for i in Locations if i != j) == 1 for j in Customers), "EnterCustomerOnce")
    model.addConstrs((gp.quicksum(x[j, i] for i in Locations if i != j) == 1 for j in Customers), "LeaveCustomerOnce")
    model.addConstr(t[Depot] == DepartureTime, "SetDepartureTime")

    # Constraint 5: Time flow constraints 
    for i in Locations:
        for j in Customers:
            if i == j:
                continue
            model.addConstr(t[j] >= t[i] + ServiceTime[i] + TravelTimes[i, j] - M * (1 - x[i, j]), f"TimeFlow_{i}_{j}")

    # Constraint 6: Sub-tour elimination (MTZ)
    model.addConstr(u[Depot] == 0, "MTZ_Depot_Start")
    M_u = len(Customers) + 1 # A sufficiently large number for the u sequence

    for i in Locations:
        for j in Customers:
            if i == j:
                continue
            # If courier travels from i to j, then u[j] must be at least u[i] + 1
            model.addConstr(u[j] >= u[i] + 1 - M_u * (1 - x[i, j]), f"MTZ_Flow_{i}_{j}")

    # Add bounds on u for customers to strengthen the formulation
    model.addConstrs((u[i] >= 1 for i in Customers), "MTZ_Lower_Bound")
    model.addConstrs((u[i] <= len(Customers) for i in Customers), "MTZ_Upper_Bound")

    # --- Solve the Model ---
    model.optimize()

    # --- Return Results ---
    if model.Status == GRB.OPTIMAL:
        return {"status": "optimal", "obj": model.ObjVal}
    else:
        return {"status": f"{model.Status}"}


if __name__ == "__main__":
    result = solve_courier_routing()
    print(result)
