import gurobipy as gp
from gurobipy import GRB
import math

def solve_waste_collection_routing(
    collection_points_num=15,
    truck_num=4,
    truck_capacity=100,
    truck_max_distance=45,
    nodes_coordinates=[
        (0, 0), (3, -8), (-10, -5), (3, 0), (-3, -3), (5, -1),
        (-7, 3), (5, -3), (4, -5), (6, 4), (3, 1), (-10, 0),
        (-2, 7), (-6, -3), (-8, -7), (-1, 8)
    ],
    collection_points_demand=[
        20, 20, 28, 12, 30, 24, 30, 12, 27, 26, 21, 20, 12, 16, 19
    ]
):
    """
    Solves the vehicle routing problem for waste collection.
    """
    model = gp.Model("WasteCollectionRouting")

    dump = 0
    collection_points = range(1, collection_points_num + 1)
    nodes = [dump] + list(collection_points)
    trucks = range(1, truck_num + 1)

    distance = {}
    for i in nodes:
        for j in nodes:
            if i != j:
                x1, y1 = nodes_coordinates[i]
                x2, y2 = nodes_coordinates[j]
                distance[i, j] = math.sqrt((x2 - x1)**2 + (y2 - y1)**2)

    x = {}
    for i in nodes:
        for j in nodes:
            if i != j:
                for k in trucks:
                    x[i, j, k] = model.addVar(vtype=GRB.BINARY, name=f"x_{i}_{j}_{k}")

    objective = gp.quicksum(distance[i, j] * x[i, j, k] for i in nodes for j in nodes for k in trucks if i != j)
    model.setObjective(objective, GRB.MINIMIZE)

    for j in collection_points:
        model.addConstr(gp.quicksum(x[i, j, k] for i in nodes if i != j for k in trucks) == 1, name=f"visit_point_{j}")

    for i in collection_points:
        for k in trucks:
            model.addConstr(gp.quicksum(x[i, j, k] for j in nodes if j != i) - gp.quicksum(x[j, i, k] for j in nodes if j != i) == 0, name=f"flow_conservation_{i}_{k}")

    for k in trucks:
        model.addConstr(gp.quicksum(x[dump, j, k] for j in collection_points) == 1, name=f"truck_{k}_leaves_dump")
        model.addConstr(gp.quicksum(x[j, dump, k] for j in collection_points) == 1, name=f"truck_{k}_returns_dump")

    for k in trucks:
        model.addConstr(gp.quicksum(collection_points_demand[j - 1] * gp.quicksum(x[i, j, k] for i in nodes if i != j) for j in collection_points) <= truck_capacity, name=f"truck_{k}_capacity")

    for k in trucks:
        model.addConstr(gp.quicksum(distance[i, j] * x[i, j, k] for i in nodes for j in nodes if i != j) <= truck_max_distance, name=f"truck_{k}_max_distance")

    def find_subtours(x_vals, k):
        edges = [(i, j) for i in collection_points for j in collection_points if i != j and x_vals.get((i, j, k), 0) > 0.5]
        if not edges:
            return []

        neighbors = {i: [] for i in collection_points}
        for i, j in edges:
            neighbors[i].append(j)

        unvisited = set(collection_points)
        subtours = []
        while unvisited:
            curr = next(iter(unvisited))
            tour = [curr]
            unvisited.remove(curr)

            while True:
                neighbors_of_curr = [j for j in neighbors.get(curr, []) if j in unvisited]
                if not neighbors_of_curr:
                    break
                curr = neighbors_of_curr[0]
                tour.append(curr)
                unvisited.remove(curr)

            is_valid_tour = any(x_vals.get((dump, i, k), 0) > 0.5 for i in tour) and any(x_vals.get((i, dump, k), 0) > 0.5 for i in tour)
            if not is_valid_tour and tour:
                subtours.append(tour)
        return subtours

    def subtour_elimination_callback(model, where):
        if where == GRB.Callback.MIPSOL:
            x_vals = model.cbGetSolution(x)
            for k in trucks:
                subtours = find_subtours(x_vals, k)
                for subtour in subtours:
                    if len(subtour) < collection_points_num:
                        model.cbLazy(gp.quicksum(x[i, j, k] for i in subtour for j in subtour if i != j) <= len(subtour) - 1)

    model.Params.lazyConstraints = 1
    model.optimize(subtour_elimination_callback)

    if model.status == GRB.OPTIMAL:
        return {"status": "optimal", "obj": model.objVal}
    else:
        return {"status": f"{model.status}"}

if __name__ == "__main__":
    result = solve_waste_collection_routing()
    print(result)

import gurobipy as gp
from gurobipy import GRB
import math

def solve_vehicle_routing(
    customer_num=12,
    truck_num=5,
    truck_capacity=15,
    node_coordinates=[
        (0, 0), (4.36, -2.75), (-7.29, -3.22), (-8.74, -2.47),
        (-0.68, -5.15), (-0.39, -3.49), (-6.06, -5.52),
        (-2.67, 9.32), (9.91, 7.87), (2.03, -2.98), (2.33, -1.47),
        (5.1, -2.08), (-8.04, -9.78)
    ],
    customer_demand=[6, 7, 1, 10, 8, 7, 3, 6, 5, 2, 4, 4]
):
    """
    Solves the vehicle routing problem.
    """
    model = gp.Model("VehicleRoutingProblem")

    center = 0
    customers = range(1, customer_num + 1)
    nodes = [center] + list(customers)
    trucks = range(1, truck_num + 1)

    distance = {}
    for i in nodes:
        for j in nodes:
            if i != j:
                x1, y1 = node_coordinates[i]
                x2, y2 = node_coordinates[j]
                distance[i, j] = math.sqrt((x2 - x1)**2 + (y2 - y1)**2)

    x = {}
    for i in nodes:
        for j in nodes:
            if i != j:
                for k in trucks:
                    x[i, j, k] = model.addVar(vtype=GRB.BINARY, name=f"x_{i}_{j}_{k}")

    objective = gp.quicksum(distance[i, j] * x[i, j, k] for i in nodes for j in nodes for k in trucks if i != j)
    model.setObjective(objective, GRB.MINIMIZE)

    for j in customers:
        model.addConstr(gp.quicksum(x[i, j, k] for i in nodes if i != j for k in trucks) == 1, name=f"customer_visited_{j}")

    for i in customers:
        for k in trucks:
            model.addConstr(gp.quicksum(x[i, j, k] for j in nodes if i != j) - gp.quicksum(x[j, i, k] for j in nodes if i != j) == 0, name=f"flow_conservation_{i}_{k}")

    for k in trucks:
        model.addConstr(gp.quicksum(x[center, j, k] for j in customers) <= 1, name=f"truck_{k}_leaves_center")
        model.addConstr(gp.quicksum(x[center, j, k] for j in customers) == gp.quicksum(x[j, center, k] for j in customers), name=f"truck_{k}_returns_center")

    for k in trucks:
        model.addConstr(gp.quicksum(customer_demand[j - 1] * gp.quicksum(x[i, j, k] for i in nodes if i != j) for j in customers) <= truck_capacity, name=f"truck_{k}_capacity")

    def find_subtours(x_vals, k):
        edges = [(i, j) for i in customers for j in customers if i != j and x_vals.get((i, j, k), 0) > 0.5]
        if not edges:
            return []

        neighbors = {i: [] for i in customers}
        for i, j in edges:
            neighbors[i].append(j)

        unvisited = set(customers)
        subtours = []
        while unvisited:
            curr = next(iter(unvisited))
            tour = [curr]
            unvisited.remove(curr)

            while True:
                neighbors_of_curr = [j for j in neighbors.get(curr, []) if j in unvisited]
                if not neighbors_of_curr:
                    break
                curr = neighbors_of_curr[0]
                tour.append(curr)
                unvisited.remove(curr)

            is_valid_tour = any(x_vals.get((center, i, k), 0) > 0.5 for i in tour) and any(x_vals.get((i, center, k), 0) > 0.5 for i in tour)
            if not is_valid_tour and tour:
                subtours.append(tour)
        return subtours

    def subtour_elimination_callback(model, where):
        if where == GRB.Callback.MIPSOL:
            x_vals = model.cbGetSolution(x)
            for k in trucks:
                subtours = find_subtours(x_vals, k)
                for subtour in subtours:
                    if len(subtour) < customer_num:
                        model.cbLazy(gp.quicksum(x[i, j, k] for i in subtour for j in subtour if i != j) <= len(subtour) - 1)

    model.Params.lazyConstraints = 1
    model.optimize(subtour_elimination_callback)

    if model.status == GRB.OPTIMAL:
        return {"status": "optimal", "obj": model.objVal}
    else:
        return {"status": f"{model.status}"}

if __name__ == "__main__":
    result = solve_vehicle_routing()
    print(result)

import gurobipy as gp
from gurobipy import GRB

def solve_truck_loading(
    truck_num=12,
    goods_num=100,
    truck_max_capacity_side=[5250, 4750],
    truck_max_weight_difference=500,
    truck_max_thickness=500,
    goods_thickness=[
        108, 62, 121, 31, 37, 90, 49, 49, 53, 156, 37, 62, 85, 78, 110, 119, 58,
        159, 62, 150, 97, 131, 88, 89, 146, 50, 69, 58, 106, 138, 105, 95, 131, 38,
        38, 92, 41, 40, 76, 133, 101, 103, 82, 134, 36, 126, 73, 121, 30, 116, 104,
        140, 116, 74, 59, 66, 80, 64, 124, 100, 65, 47, 96, 83, 146, 139, 36, 47,
        58, 133, 93, 60, 41, 38, 105, 31, 119, 115, 98, 93, 37, 90, 121, 83, 92,
        36, 32, 56, 74, 126, 117, 72, 43, 38, 78, 65, 112, 149, 33, 88
    ],
    goods_weight=[
        469, 301, 673, 239, 451, 723, 513, 417, 397, 1715, 261, 578, 916, 736,
        1024, 634, 346, 1038, 428, 1008, 709, 1396, 876, 694, 737, 398, 656, 356,
        1165, 1112, 729, 1070, 1015, 224, 437, 917, 273, 303, 614, 738, 1069, 636,
        686, 1035, 188, 1103, 647, 1269, 271, 1234, 631, 1577, 1201, 614, 435, 564,
        695, 375, 1541, 693, 413, 270, 482, 979, 1461, 1251, 329, 389, 606, 1491,
        445, 493, 357, 403, 706, 196, 1405, 571, 1097, 872, 279, 581, 973, 814,
        585, 221, 235, 664, 817, 929, 951, 509, 434, 339, 525, 499, 1250, 1419,
        140, 720
    ]
):
    """
    Solves the truck loading problem.
    """
    model = gp.Model("TruckLoadingProblem")

    trucks = range(1, truck_num + 1)
    goods = range(1, goods_num + 1)
    sides = [0, 1]

    x = {}
    for g in goods:
        for k in trucks:
            for s in sides:
                x[g, k, s] = model.addVar(vtype=GRB.BINARY, name=f"x_{g}_{k}_{s}")

    y = {}
    for k in trucks:
        y[k] = model.addVar(vtype=GRB.BINARY, name=f"y_{k}")

    objective = gp.quicksum(y[k] for k in trucks)
    model.setObjective(objective, GRB.MINIMIZE)

    for g in goods:
        model.addConstr(gp.quicksum(x[g, k, s] for k in trucks for s in sides) == 1, name=f"good_{g}_assigned_once")

    for k in trucks:
        for s in sides:
            model.addConstr(gp.quicksum(goods_weight[g - 1] * x[g, k, s] for g in goods) <= truck_max_capacity_side[s], name=f"truck_{k}_side_{s}_capacity")

    for k in trucks:
        model.addConstr(gp.quicksum(goods_weight[g - 1] * x[g, k, 0] for g in goods) - gp.quicksum(goods_weight[g - 1] * x[g, k, 1] for g in goods) <= truck_max_weight_difference, name=f"truck_{k}_side_difference_upper")
        model.addConstr(gp.quicksum(goods_weight[g - 1] * x[g, k, 0] for g in goods) - gp.quicksum(goods_weight[g - 1] * x[g, k, 1] for g in goods) >= 0, name=f"truck_{k}_side_difference_lower")

    for k in trucks:
        for s in sides:
            model.addConstr(gp.quicksum(goods_thickness[g - 1] * x[g, k, s] for g in goods) <= truck_max_thickness, name=f"truck_{k}_side_{s}_thickness")

    for k in trucks:
        model.addConstr(gp.quicksum(x[g, k, s] for g in goods for s in sides) >= y[k], name=f"truck_{k}_used_if_loaded_1")

    for g in goods:
        for k in trucks:
            for s in sides:
                model.addConstr(y[k] >= x[g, k, s], name=f"truck_{k}_used_if_loaded_2_{g}_{s}")

    model.optimize()

    if model.status == GRB.OPTIMAL:
        return {"status": "optimal", "obj": model.objVal}
    else:
        return {"status": f"{model.status}"}

if __name__ == "__main__":
    result = solve_truck_loading()
    print(result)

import gurobipy as gp
from gurobipy import GRB
import math

def solve_MultiDepotVRP(
    customer_num = 10,
    center_num = 2,
    customer_demand = [2, 2, 9, 1, 4, 4, 10, 6, 4, 4],
    truck_num = [3, 3],  # 3 trucks at DC 1, 3 trucks at DC 2
    truck_capacity = [20, 18],  # Capacity of trucks at DC 1 and DC 2
    customer_coord = [(-4.53, -5.12), (2.14, 5.85), (-4.88, -8.33), (2.24, 1.63),
                    (3.91, -5.88), (-1.1, 0.25), (7.92, -3.4), (-1.89, -5.0),
                    (0.72, -8.8), (-1.88, -2.89)],
    center_coord = [(-5, -5), (5, 5)]
):
    # --- Model Initialization ---
    model = gp.Model("MultiDepotVRP")


    # Define sets
    customers = range(1, customer_num + 1)  # Customers are 1 to 10
    dcs = range(customer_num + 1,
                customer_num + center_num + 1)  # DCs are 11 and 12
    nodes = list(customers) + list(dcs)  # All nodes

    # Create trucks for each distribution center
    all_trucks = []
    dc_to_trucks = {}
    for i, dc in enumerate(dcs):
        dc_trucks = list(range(sum(truck_num[:i]) + 1, sum(truck_num[:i + 1]) + 1))
        dc_to_trucks[dc] = dc_trucks
        all_trucks.extend(dc_trucks)

    # Create a list of all coordinates for visualization
    all_coords = {}
    for i, coord in enumerate(customer_coord):
        all_coords[i + 1] = coord  # Customer i+1 has coordinates coord
    for i, coord in enumerate(center_coord):
        all_coords[customer_num + i + 1] = coord  # DC i+1 has coordinates coord

    # Calculate distances between nodes
    distance = {}
    for i in nodes:
        for j in nodes:
            if i != j:  # No self-loops
                x1, y1 = all_coords[i]
                x2, y2 = all_coords[j]
                distance[i, j] = math.sqrt((x2 - x1)**2 + (y2 - y1)**2)

    # --- Decision variables ---
    # x[i,j,k] = 1 if truck k travels from node i to node j, 0 otherwise
    x = {}
    for i in nodes:
        for j in nodes:
            if i != j:  # No self-loops
                for k in all_trucks:
                    x[i, j, k] = model.addVar(vtype=GRB.BINARY,
                                            name=f"x_{i}_{j}_{k}")

    # --- Objective function ---
    # minimize total distance traveled
    objective = gp.quicksum(distance[i, j] * x[i, j, k] for i in nodes
                            for j in nodes for k in all_trucks if i != j)
    model.setObjective(objective, GRB.MINIMIZE)

    # --- Constraints ---
    # Constraint 1: Each customer must be visited exactly once (by one truck)
    for j in customers:
        model.addConstr(gp.quicksum(x[i, j, k] for i in nodes if i != j
                                    for k in all_trucks) == 1,
                        name=f"customer_visited_{j}")

    # Constraint 2: Flow conservation - a truck that enters a customer must also leave it
    for i in customers:
        for k in all_trucks:
            model.addConstr(gp.quicksum(x[i, j, k] for j in nodes if j != i) -
                            gp.quicksum(x[j, i, k] for j in nodes if j != i) == 0,
                            name=f"flow_conservation_{i}_{k}")

    # Constraint 3: Each truck leaves its distribution center exactly once
    for d in dcs:
        for k in dc_to_trucks[d]:
            model.addConstr(gp.quicksum(x[d, j, k] for j in nodes if j != d) == 1,
                            name=f"truck_{k}_leaves_dc_{d}")

    # Constraint 4: Each truck returns to its distribution center
    for d in dcs:
        for k in dc_to_trucks[d]:
            model.addConstr(gp.quicksum(x[i, d, k] for i in customers) -
                            gp.quicksum(x[d, j, k] for j in customers) == 0,
                            name=f"truck_{k}_returns_to_dc_{d}")

    # Constraint 5: Capacity constraint for each truck
    for d_idx, d in enumerate(dcs):
        for k in dc_to_trucks[d]:
            model.addConstr(gp.quicksum(
                customer_demand[j - 1] * gp.quicksum(x[i, j, k]
                                                    for i in nodes if i != j)
                for j in customers) <= truck_capacity[d_idx],
                            name=f"truck_{k}_capacity")


    # Function to find subtours in the current solution
    def find_subtours(x_vals, k):
        """Find subtours in the current solution for truck k"""
        # Create an edge list from the solution
        edges = [(i, j) for i in nodes for j in nodes
                if i != j and (i, j, k) in x_vals and x_vals[i, j, k] > 0.5]

        if not edges:
            return []

        # Create an undirected graph for finding connected components
        neighbors = {i: [] for i in nodes}
        for i, j in edges:
            neighbors[i].append(j)

        # Find all connected components (subtours)
        unvisited = set(customers)  # Only consider customer nodes for subtours
        subtours = []

        while unvisited:
            # Start a new tour with an unvisited node
            if not unvisited:
                break
            curr = next(iter(unvisited))
            tour = [curr]
            unvisited.remove(curr)

            # Build the tour
            while neighbors[curr]:
                neighbors_of_curr = [j for j in neighbors[curr] if j in unvisited]
                if not neighbors_of_curr:
                    break

                curr = neighbors_of_curr[0]
                if curr in unvisited:  # Only add if not already in tour
                    tour.append(curr)
                    unvisited.remove(curr)

            # Check if it's a subtour (not connected to any distribution center)
            is_connected_to_dc = any(d in neighbors[node] for node in tour
                                    for d in dcs)

            if not is_connected_to_dc and len(tour) > 1:
                subtours.append(tour)

        return subtours


    # Callback function for lazy constraints
    def subtour_elimination_callback(model, where):
        if where == GRB.Callback.MIPSOL:
            # Get the current solution values
            x_vals = {}
            for i in nodes:
                for j in nodes:
                    if i != j:
                        for k in all_trucks:
                            if (i, j, k) in x:
                                val = model.cbGetSolution(x[i, j, k])
                                if val > 0.5:
                                    x_vals[i, j, k] = val

            # For each truck, find subtours and add constraints
            for k in all_trucks:
                subtours = find_subtours(x_vals, k)
                for subtour in subtours:
                    if len(subtour
                        ) >= 2:  # Only add constraints for actual subtours
                        # Add subtour elimination constraint
                        model.cbLazy(
                            gp.quicksum(x[i, j, k] for i in subtour
                                        for j in subtour
                                        if i != j) <= len(subtour) - 1)


    # Enable lazy constraints
    model._vars = x
    model.Params.lazyConstraints = 1

    # Optimize the model with the callback
    model.optimize(subtour_elimination_callback)

    # Check if the model was solved to optimality
    if model.status == GRB.OPTIMAL:
        return {"status": "optimal", "obj": model.objVal}
    else:
        return {"status": f"{model.status}"}

if __name__ == "__main__":
    result = solve_MultiDepotVRP()
    print(result)

