import coptpy as cp
from coptpy import COPT
import math

# Create a COPT environment
env = cp.Envr()

# Create a model
model = env.createModel("CityDeliveryOptimization")

# Distribution center coordinates
distribution_centers = {'A': (-5, -5), 'B': (5, 5)}

# Customer coordinates and demands
customers = {(-4.53, -5.12): 2, (2.14, 5.85): 2, (-4.88, -8.33): 9, (2.24, 1.63): 1, 
             (3.91, -5.88): 4, (-1.1, 0.25): 4, (7.92, -3.4): 10, (-1.89, -5.0): 6, 
             (0.72, -8.8): 4, (-1.88, -2.89): 4}

# Truck capacity
truck_capacity = 20

# Add decision variables
x = {(i, j, k): model.addVar(vtype=COPT.BINARY, name=f"x_{i}_{j}_{k}") for i in distribution_centers for j in customers for k in range(1, 4)}
y = {(k, l): model.addVar(vtype=COPT.BINARY, name=f"y_{k}_{l}") for k in range(1, 4) for l in distribution_centers}

# Objective function: Minimize total travel distance
model.setObjective(cp.quicksum(math.sqrt((distribution_centers[i][0] - customers[j][0])**2 + (distribution_centers[i][1] - customers[j][1])**2) * x[i, j, k] +
                               math.sqrt((customers[j][0] - distribution_centers[l][0])**2 + (customers[j][1] - distribution_centers[l][1])**2) * y[k, l]
                               for i in distribution_centers for j in customers for k in range(1, 4) for l in distribution_centers), sense=COPT.MINIMIZE)

