import gurobipy as gp
from gurobipy import GRB
import itertools


def solve_capacitated_mst(
    nodes=list(range(9)),
    warehouse=0,
    distances_data=None,
    capacity=3,
    time_limit=60
):
    """
    Solves the capacitated minimal spanning tree problem using a
    single-commodity flow formulation.
    """
    if distances_data is None:
        distances = {
            (0, 1): 12, (0, 2): 8, (1, 2): 5, (1, 3): 9, (2, 4): 7, (3, 4): 6,
            (3, 5): 11, (4, 6): 10, (5, 6): 4, (5, 7): 8, (6, 8): 7, (7, 8): 6
        }
        for i, j in distances.copy():
            distances[j, i] = distances[i, j]
    else:
        distances = distances_data

    stores = [n for n in nodes if n != warehouse]
    demands = {n: 1 if n in stores else -(len(stores)) for n in nodes}

    model = gp.Model("Corrected_CMST")

    x = model.addVars(distances.keys(), vtype=GRB.BINARY, name="edge_selected")
    f = model.addVars(distances.keys(), vtype=GRB.CONTINUOUS, name="flow")

    model.setObjective(
        gp.quicksum(distances[i, j] * x[i, j] for i, j in distances if i < j),
        GRB.MINIMIZE
    )

    model.addConstr(x.sum('*', '*') / 2 == len(nodes) - 1, "num_edges")

    for i in nodes:
        in_flow = gp.quicksum(f[j, i] for j in nodes if (j, i) in distances)
        out_flow = gp.quicksum(f[i, j] for j in nodes if (i, j) in distances)
        model.addConstr(in_flow - out_flow == demands[i], f"flow_balance_{i}")

    for i, j in distances:
        model.addConstr(f[i, j] <= (len(nodes) - 1) * x[i, j], f"flow_link_{i}_{j}")

    for i, j in distances:
        if i != warehouse and j != warehouse:
            model.addConstr(f[i, j] + f[j, i] <= capacity * x[i, j], f"capacity_{i}_{j}")

    model.Params.TimeLimit = time_limit
    model.optimize()

    if model.Status == GRB.OPTIMAL or model.Status == GRB.TIME_LIMIT:
        return {"status": "optimal", "obj": model.ObjVal}
    else:
        return {"status": f"{model.Status}"}


if __name__ == "__main__":
    result = solve_capacitated_mst()
    print(result)

import gurobipy as gp
from gurobipy import GRB


def solve_logistics_problem(
    orders=list(range(1, 9)),
    trucks=list(range(1, 4)),
    processing_time=[4, 5, 3, 6, 2, 5, 4, 3],
    reentrant_factor=[1, 2, 1, 1, 2, 1, 1, 1],
    truck_capacity=[15, 18, 12],
    operating_cost=[30, 25, 35]
):
    """Solve the truck scheduling problem with reentrant processing requirements."""
    model = gp.Model("LogisticsScheduling")

    x = model.addVars(orders, trucks, vtype=GRB.BINARY, name="Assign")
    y = model.addVars(trucks, vtype=GRB.BINARY, name="TruckUsed")

    model.setObjective(
        gp.quicksum(
            gp.quicksum(
                processing_time[o - 1] * reentrant_factor[o - 1] * x[o, t] * operating_cost[t - 1]
                for o in orders
            )
            for t in trucks
        ),
        GRB.MINIMIZE
    )

    model.addConstrs((gp.quicksum(x[o, t] for t in trucks) == 1 for o in orders))

    model.addConstrs(
        (gp.quicksum(
            processing_time[o - 1] * reentrant_factor[o - 1] * x[o, t]
            for o in orders
        ) <= truck_capacity[t - 1]
        for t in trucks),
        name="Capacity"
    )

    M = len(orders)
    model.addConstrs((gp.quicksum(x[o, t] for o in orders) <= M * y[t] for t in trucks))

    model.optimize()

    if model.status == GRB.OPTIMAL:
        return {"status": "optimal", "obj": model.ObjVal}
    else:
        return {"status": f"{model.status}"}


if __name__ == "__main__":
    result = solve_logistics_problem()
    print(result)

